define([
    'jquery',
    'Dadolun_Checkout/js/utils/field-watcher'
], function ($, watcher) {
    'use strict';

    // DO NOT EDIT THIS FILE - MUST REMAIN EMPTY FOR THEME OVERRIDES
    // DOCS: https://github.com/dadolun95/magento2-checkout-customizer/blob/master/README.md

    watcher(
        'billing-address-form',
        'company',
        {
            value: 'Test',
            match: function(component) {
                console.log('Billing company has "Test" value.');
            },
            unmatch: function(component) {
                console.log('Billing company has not "Test" value.');
            }
        }
    );

});
